   "source": [
    "# Load data\n",
    "netflix <- read.csv(\"netflix_ratings.csv\")\n",
    "\n",
    "# Remove missing ratings\n",
    "netflix <- subset(netflix, !is.na(rating))\n",
    "\n",
    "# Count rating frequencies\n",
    "rating_counts <- table(netflix$rating)\n",
    "\n",
    "# Sort ratings by frequency (descending)\n",
    "sorted_ratings <- sort(rating_counts, decreasing = TRUE)\n",
    "\n",
    "# Convert to data frame for ggplot\n",
    "df_ratings <- as.data.frame(sorted_ratings)\n",
    "colnames(df_ratings) <- c(\"Rating\", \"Count\")\n",
    "\n",
    "# Plotting\n",
    "library(ggplot2)\n",
    "\n",
    "ggplot(df_ratings, aes(x = reorder(Rating, -Count), y = Count)) +\n",
    "  geom_bar(stat = \"identity\", fill = \"steelblue\") +\n",
    "  labs(title = \"Distribution of Netflix Content by Rating\",\n",
    "       x = \"Rating\",\n",
    "       y = \"Number of Titles\") +\n",
    "  theme_minimal() +\n",
    "  coord_flip()\n",
    "\n",
    "# Save plot\n",
    "ggsave(\"ratings_distribution_R.png\")"
   ]
